import React from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import BoardList from '../BoardList';

class Board extends React.Component {
    state = {
        Boards: []
    }

    getBoardList = async () => {
        const {
            data: { data }
        } = await axios.get("http://localhost:8080/boardlist");
        console.log(data);
        this.setState({ Boards: data });
    }

    componentDidMount() {
        this.getBoardList();
    }
    render() {
        const { Boards } = this.state;
        return (
            <>
                <div className="container-fluid">
                    <Link to="/write" className="btn btn-success justify-content-end">글쓰기</Link>
                </div>
                <div>
                    <table className="table table-hover">
                        <tr>
                            <th>번호</th>
                            <th>제목</th>
                            <th>작성자</th>
                            <th>작성일자</th>
                            <th>조회수</th>
                        </tr>
                        {Boards.map(function (Board) {
                            return <BoardList no={Board.no} title={Board.title} name={Board.name} writedate={Board.writedate} readcount={Board.readcount} />
                        })}

                    </table>
                </div>
            </>
        );

    }
}

export default Board;
