import React from 'react';
import axios from 'axios';
import {Link} from 'react-router-dom';

class Board extends React.Component{
    state = {
        Boards: []
    }

    getBoardList = async () =>{
        const data = axios.get("http://localhost:8080/board");
        this.setState({Boards: data});
    }

    componentDidMount(){
        this.getBoardList();
    }
    render(){
        const {Boards} = this.state;
        return (
            <div className="container">
                <button className="btn-sm btn-success justify-content-end" value="글쓰기"><Link to="/write"></Link></button>
            <table className="table table-hover table-striped">
                <thead>
                    <td>번호</td>
                    <td>제목</td>
                    <td>작성자</td>
                    <td>작성일자</td>
                    <td>조회수</td>
                </thead>
                <tbody>
                    {Boards.map(function(Board){
                        return (
                            <tr>
                                <Link to={
                                    {
                                        pathname: `/board/${Board.no}`,
                                        state: {
                                            no: Board.no,
                                            title: Board.title,
                                            name: Board.name,
                                            content: Board.content,
                                            pass: Board.pass
                                        }
                                    }
                                }>
                                    <td>{Board.no}</td>
                                    <td>{Board.title}</td>
                                    <td>{Board.name}</td>
                                    <td>{Board.writedate}</td>
                                    <td>{Board.readcount}</td>
                                </Link>
                            </tr>
                        );
                    })}
                </tbody>
            </table>
            </div>
        );
    }
}

export default Board;
