import React from 'react';
import axios from 'axios';
import '../css/ProductCard.css';
import ProductCard from '../ProductCard';

class Product extends React.Component{
    state={
        product: []
    };

    getProduct = async () => {
        const {
            data: {data}
        } = await axios.get("http://localhost:8080/todo");
        this.setState({product: data});
    }

    componentDidMount(){
        this.getProduct();
    }

    render(){
        const {product} = this.state;
    return <div className="cardbody container">
        <header class="jumbotron my-4">
            <h1 class="display-3">Spring Project를 진행중입니다!</h1>
            <p class="lead">이 페이지는 관리자만이 업로드 할 수있습니다.</p>
        </header>
        {product.map(function(prod){
            return <div key={prod.num} className="row text-center">
                <ProductCard num={prod.num} imgaddr={prod.imgaddr} content={prod.content} title={prod.title}/>
            </div>;
        })}
    </div>;
    }
}

export default Product;
