import React from 'react';
import crypto from 'crypto';

class Signup extends React.Component{
    state = {

    }
    
    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    check_pass = (e) => {
        const {pass, pass_check} = this.state;
        console.log(pass);
        if(pass === pass_check){
            return true;
        }else{
            e.preventDefault();
        }
    }

    signup = (e) => {
        e.preventDefault();
        if(this.check_pass){
            var {pass} = this.state;
        }
        crypto.randomBytes(64,(err, buf) => {
            crypto.pbkdf2({pass}, buf.toString('base64'), 100000,64,'sha512' ,(err,key) => {
                console.log(key.toString('base64'));
            });
        });
    }


    render(){
        return (
                <form method="post" onSubmit={this.signup}>
                    <table>
                        <tbody>
                            <tr className="row">
                                <td className="col"><label htmlFor="id">ID</label><input type="text" name="id" id="id" className="form-control" /></td>
                            </tr>
                            <tr className="row">
                                <td className="col"><label htmlFor="pass">PassWord</label><input type="password" name="pass" id="pass" className="form-control" /></td>
                            </tr>

                            <tr className="row">
                                <td className="col"><label htmlFor="pass_check">PassWord Check</label><input type="password" name="pass_check" id="pass_check" className="form-control" /></td>
                            </tr>
                            <tr className="row">
                                <td className="col"><label htmlFor="name">Name</label><input type="text" name="name" id="name" className="form-control" /></td>
                            </tr>
                            <tr className="row">
                                <td className="col"><input type="submit" value="회원가입" className="btn bg-success" /></td>
                            </tr>
                        </tbody>
                    </table>
                </form>
        );
    }
}

export default Signup;
